import paramiko, base64, io

hostname = "sftp-int.mercadolibre.io"
port = 22
username = "jrimmele_mp_prod"
ssh_key_b64 = ""
ssh_secret = "juanmartin123"

decoded_key = base64.b64decode(ssh_key_b64).decode("utf-8")
key = paramiko.RSAKey.from_private_key(io.StringIO(decoded_key), password=ssh_secret)

transport = paramiko.Transport((hostname, port))
transport.connect(username=username, pkey=key)
sftp = paramiko.SFTPClient.from_transport(transport)

print("✅ Conexión establecida")
print("Archivos:", sftp.listdir("mi/mla/bind/"))

sftp.close()
transport.close()
